if (typeof tempVars("emitType") == 'undefined') {
   var emitReq = "nothing";
} else {
   var emitReq = tempVars("emitType").toUpperCase();
};

switch(emitReq) {
	case "MEMBERJOIN": 
		this.getDBM().Bot.bot.emit("guildMemberAdd", msg.member); 
		msg.channel.send("I have emitted `guildMemberAdd`"); 
		break;
	case "MEMBERLEAVE":
		this.getDBM().Bot.bot.emit("guildMemberRemove", msg.member); 
		msg.channel.send("I have emitted `guildMemberRemove`"); 
		break;
	case "BAN": 
		this.getDBM().Bot.bot.emit("guildBanAdd", msg.guild, msg.author); 
		msg.channel.send("I have emitted `guildBanAdd`"); 
		break;
	case "UNBAN": 
		this.getDBM().Bot.bot.emit("guildBanRemove", msg.guild, msg.author); 
		msg.channel.send("I have emitted `guildBanRemove`"); 
		break;
	case "ERROR": 
		msg.channel.send("I have emitted `error` - please check your log for details");
		this.getDBM().Bot.bot.emit("error", "This is a test error"); 
		break;
	default: 
		msg.channel.send("The syntax for this command is `?emit <type>` \n\n**The types you can choose from are:** \n`memberjoin` - emitted when a member joins the guild \n`memberleave` - emitted when a member leaves the guild (either willingly or via kick) \n`ban` - emitted when a member is banned \n`unban` - emitted when a member is banned \n`error` - throws an uncaught error - if you use Lasse's custom script it will log to console");
		break;
};

