const { RichEmbed } = require('discord.js')
const { inspect } = require('util')
const embed = new RichEmbed()
  .setFooter(msg.author.tag, msg.author.displayAvatarURL)
const args = msg.content.slice(this.getDBM().Files.data.settings.tag.length).trim().split(/ +/g); args.shift()
const query = args.join(' ')
if (query) {
  const code = (lang, code) => (`\`\`\`${lang}\n${String(code).slice(0, 1000) + (code.length >= 1000 ? '...' : '')}\n\`\`\``).replace(client ? client.token : this.getDBM().Bot.bot.token, '*'.repeat((client ? client.token : this.getDBM().Bot.bot.token).length))
  try {
    const evald = eval(query)
    const res = (typeof evald === 'string' ? evald : inspect(evald, { depth: 0 }))
    embed.addField('Result', code('js', res))
      .addField('Type', code('css', typeof evald === 'undefined' ? 'Unknown' : typeof evald))
      .setColor('GREEN')
  } catch (err) {
    embed.addField('Error', code('js', err))
      .setColor('RED')
  } finally {
    msg.channel.send(embed)
      .catch(err => {
        msg.channel.send(`There was an error while displaying the eval result! ${err.message}`)
      })
  }
} else {
  msg.channel.send('Please, write something so I can evaluate!')
}

